sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isPastDate: function(strDate){
		var currentDate = GlideDateTime();
		var whenNeededDate = GlideDateTime(strDate);
		if(whenNeededDate.before(currentDate))
			{
				return true;
			}
		else
			{
				return false;
			}

	},
	isDateToday: function(strDate){
		var currentDate = GlideDateTime();
		var whenNeededDate = GlideDateTime(strDate);
		if(whenNeededDate.getDate()==currentDate.getDate())
			{
				return true;
			}
		else
			{
				return false;
			}
	},
    type: 'NeedItUtils'
};
